INSERT INTO curriculum (subject, lesson_title, lesson_skills, success_criteria, wp1_name, wp1_type, ma1_name, ma1_type, ma2_name, ma2_type, a1_name, a1_type, a2_name, a2_type) VALUES ('English', 'Starter Unit: Hello!', 'say hello and make new friends, name parts of the body', 'Students can introduce themselves and name parts of the body.', 'Learn a \'hello\' poem', 'poem', 'Listen, point and say (boys talking)', 'listening', 'Listen, point and say (girls talking)', 'listening', 'Head, shoulders, knees and toes', 'song', 'Play \'Simon Says!\'', 'game');
